from selenium import webdriver
from selenium.webdriver.common.keys import Keys
from selenium.webdriver.common.by import By
from selenium.webdriver.support.wait import WebDriverWait
from selenium.webdriver.support import expected_conditions

class CopartSearchBar:
    def __init__(self,driver):
        self.driver=driver 
        self.init_var()

    def search_input (self,search):
        self.searchbar.click()
        self.searchbar.send_keys(str(search))
        self.searchbar.send_keys(Keys.RETURN)
        WebDriverWait(self.driver, 10).until(
            expected_conditions.visibility_of_element_located((By.XPATH,"//*[@id='serverSideDataTable']/tbody"))
        )

    def init_var (self):
        self.searchbar = self.driver.find_element(By.XPATH,"//*[@id='input-search']")

    